var height=window.screen.height-250;var width=window.screen.width;var leftW=300;if(width>1200){leftW=500}else{if(width>1000){leftW=350}else{leftW=100}}var _html="<div id='loading' style='position:absolute;left:0;width:100%;height:"+height+"px;top:0;background:#E0ECFF;opacity:0.8;filter:alpha(opacity=80);'><div style='position:absolute;  cursor1:wait;left:"+leftW+"px;top:200px;width:auto;height:16px;padding:12px 5px 10px 30px;background:#fff url(themes/default/images/loading.gif) no-repeat scroll 5px 10px;border:2px solid #ccc;color:#000;'>\u6b63\u5728\u52a0\u8f7d\uff0c\u8bf7\u7b49\u5f85...</div></div>";window.onload=function(){var a=document.getElementById("loading");a.parentNode.removeChild(a)};document.write(_html);
