var xform;window.onload=function(){xform=new xf.Xform("xf-form");xform.addSection(new xf.TextSection("h1","Title"));xform.addSection(new xf.GridSection(2,4));xform.initEvents();xform.selectionListener={select:function(c){if(!c){return}var b=xf.$("xf-form-attribute");c.viewForm(b)}};xform.render();if($("#__gef_content__").val()!=""){try{xform.doImport($("#__gef_content__").val(),"","input-form-template")}catch(a){console.error(a);xform.sections=[];xform.addSection(new xf.TextSection("h1","Title"));xform.addSection(new xf.GridSection(2,6));xform.render()}}};function doImport(){var a=prompt("text",'{"sections":[{"type":"text","tag":"h1",text:"title"},{"type":"grid","row":"2",col:"4","fields":[{"type":"label","row":0,"col":0,"text":"11111111111"},{"type":"textfield","row":0,"col":1,"name":"test","required":true}]}]}');if(a!=""){xform.doImport(a)}}function doChangeMode(a){if(xform.mode=="EDIT"){xform.mode="MERGE";$("#_btMerge").show();$("#_btSplit").show();a.innerHTML="\u7f16\u8f91\u5185\u5bb9"}else{xform.mode="EDIT";$("#_btMerge").hide();$("#_btSplit").hide();a.innerHTML="\u7f16\u8f91\u8868\u683c";xform.doCleanSelectedItem()}}function doMerge(){if(xform.mode=="MERGE"){xform.doMerge()}}function doSplit(){if(xform.mode=="MERGE"){xform.doSplit()}};
